    }
    return (
        <Container>
            <Arrow direction="left" onClick={() => handleClick("left")}>
                <ArrowLeftOutlinedIcon/>
            </Arrow>
            <Wrapper>
                <Slide bg="f5fafd">
                <ImageContainer>
                    <Image src="https://s3.ap-south-1.amazonaws.com/www.kellyfelder.com/products/ed35b067cc305758fbd35165363143e7ae88f2ed.jpg"/>
                </ImageContainer>
                <InfoContainer>
                    <Title>Year End Sell</Title>
                    <Desc>Get 30% OFF For New Arrivals </Desc>
                    <Button>Shop Now</Button>
                </InfoContainer>
                </Slide>

                <Slide bg="fcf1fd">
                <ImageContainer>
                    <Image src="https://s3.ap-south-1.amazonaws.com/www.kellyfelder.com/products/ed35b067cc305758fbd35165363143e7ae88f2ed.jpg"/>
                </ImageContainer>
                <InfoContainer>
                    <Title>Year End Sell</Title>
                    <Desc>Get 30% OFF For New Arrivals </Desc>
                    <Button>Shop Now</Button>
                </InfoContainer>
                </Slide>

                <Slide bg="fbf0f4">
                <ImageContainer>
                    <Image src="https://s3.ap-south-1.amazonaws.com/www.kellyfelder.com/products/ed35b067cc305758fbd35165363143e7ae88f2ed.jpg"/>
                </ImageContainer>
                <InfoContainer>
                    <Title>Year End Sell</Title>
                    <Desc>Get 30% OFF For New Arrivals </Desc>
                    <Button>Shop Now</Button>
                </InfoContainer>
                </Slide>
            </Wrapper>
            <Arrow direction="right" onClick={() => handleClick("right")}>
                <ArrowRightOutlinedIcon/>
            </Arrow>
        </Container>
    )
}

export default Slider
